VALUES ('48617e77-186d-48e0-9a55-dc79cb7091ee', '111', 'Secession', 'i - s imposed at any other criminal provision, except', 'penal_code', 'en', '(1) is punishable by life imprisonment the one who, in peacetime, undertake by any means whatsoever to undermine the integrity of the territory. {2) In wartime, state of emergency or exception, the penalty is that Death.');
INSERT INTO legal_articles (id, article_id, title, book, law_type, language, text)
VALUES ('868436c6-e353-44b5-accb-460d054df7c8', '112', NULL, 'i - s imposed at any other criminal provision, except', 'penal_code', 'en', '.- Civil war Is punished with death the one who excites the civil war by arming or pushing The inhabitants to arm against each other.');
