VALUES ('14e5d14d-4ee4-47f9-82e2-08342861b253', '113', 'Propagation of false news', 'i - s imposed at any other criminal provision, except', 'penal_code', 'en', 'Is punished with imprisonment of three (03) months to three (03) years and a fine of one hundred thousand (100,000) to two million (2,000,000) francs, the one that emits or propagates false news, when these news is likely to harm public authorities or national cohesion. In the event of reduction in the sentence provided for in articles 111 (2} and 112 below Above, the private penalty is that of themprisonment.');
INSERT INTO legal_articles (id, article_id, title, book, law_type, language, text)
VALUES ('e288fe67-214f-40f9-80c2-7b2521d9932d', '114', 'Revolution', 'i - s imposed at any other criminal provision, except', 'penal_code', 'en', 'Is punished with imprisonment for life, the one who tries with violence, or modify constitutional laws, either to overthrow political authorities instituted by said laws or to put them in the impossibility of exercising their powers.');
INSERT INTO legal_articles (id, article_id, title, book, law_type, language, text)
