INSERT INTO legal_articles (id, article_id, title, book, law_type, language, text)
VALUES ('7687239a-a72c-4ed9-aa0e-a588425ea8d0', '123-1', 'Wearing of weapons during electoral period', 'i - s imposed at any other criminal provision, except', 'penal_code', 'en', '(1) is punishable by imprisonment of ten (10) days 4 two (02) months and a fine of twenty five thousand (25,000) to two hundred and fifty thousand (250 000) francs or one of these two penalties only, the one that enters a polling station with an apparent weapon. 47 (2) The prison sentence can be increased to four (04) months and The fine to Cing a hundred thousand (500,000) francs if the weapon was hidden.');
INSERT INTO legal_articles (id, article_id, title, book, law_type, language, text)
VALUES ('241db7c5-0fec-4eff-a70e-a110cb231f95', '123-2', 'Activity or manifestation of a political nature during', 'i - s imposed at any other criminal provision, except', 'penal_code', 'en', '(1) Any political activity or manifestation is prohibited within public establishments as well as in schools or university. (2) Any offense on the provisions of the above paragraph is punishable by a imprisonment of ten (10) days at four (04) months and a fine of twenty five thousand (25,000) to two hundred and fifty thousand (250,000) francs or one of These two penalties only.');
INSERT INTO legal_articles (id, article_id, title, book, law_type, language, text)
VALUES ('c792f441-10d1-4cb2-8ae0-32d7706cbf53', '123', NULL, 'i - s imposed at any other criminal provision, except', 'penal_code', 'en', '-3.- Hijage of public funds in electoral matters Is punishable by the penalties provided for in article 184 of this code, all person who, acting in his name or on behalf of a political party, uses the funds received within the framework of public funding for purposes other than those provided by law bearing Electoral Code.');
INSERT INTO legal_articles (id, article_id, title, book, law_type, language, text)
VALUES ('62fdc16a-5240-4614-82c8-84a8b083bd41', '123-4', 'Criminal proceedings in electoral morning', 'i - s imposed at any other criminal provision, except', 'penal_code', 'en', 'Except the case of flagrant offense, no criminal prosecution against a candidate for violation of the provisions of this section cannot be brought Before the proclamation of the results. Section II Coalition');
