INSERT INTO legal_articles (id, article_id, title, book, law_type, language, text)
VALUES ('29b67019-d13a-479b-981d-1a2a42d119db', '126', 'Empietary on executive power and power', 'i - s imposed at any other criminal provision, except', 'penal_code', 'en', 'Is punishable by imprisonment of six (06) months to five (05) years: - the representative of the executive authority who intimate orders or defenses to courses or courts; - the magistrate who intimate orders or defenses to authorities executive or administrative.');
