INSERT INTO legal_articles (id, article_id, title, book, law_type, language, text)
VALUES ('2d43f8d9-7b90-4c0e-a688-8ba8aa9dd83b', '128', NULL, 'i - s imposed at any other criminal provision, except', 'penal_code', 'en', '.- illegal use of force Is punishable by imprisonment of three (03) to ten (10) years and a fine from twenty thousand (20,000) to two hundred thousand (200,000) francs, the one who requires or orders the action or employment of the public force against the execution of a legislative or regulatory or legitimate order either of justice, either administration. 49');
