INSERT INTO legal_articles (id, article_id, title, book, law_type, language, text)
VALUES ('9b58f10b-1756-4cf4-a025-bd64f8713cd1', '160', 'Official constraint', 'i - s imposed at any other criminal provision, except', 'penal_code', 'en', 'Is punishable by imprisonment of two (02) a ten (10) years and a fine of twenty thousand (20,000) to one million (1,000,000) francs, the one who, by assault or threats, determines an official has an act or irregular abstention.');
INSERT INTO legal_articles (id, article_id, title, book, law_type, language, text)
VALUES ('ef5873de-6a30-4a28-abe8-834fcc65adc1', '161', NULL, 'i - s imposed at any other criminal provision, except', 'penal_code', 'en', '.- Influence trafficking (1) is punishable by the penalties of article 160 of this code, that which, by assault, threats, donations or promises, corrupts a person having real or supposed influence to obtain public authority or private, any advantage. (2) is punished with the same penalties, the civil servant or the public agent who, for itself or for others, requests, approved or receives offers, promises or donations to obtain any advantage granted by the public authority or by an organization placed under the control of the authority 61 | | public, markets, companies or other profits resulting from agreements concluded with the public authority or an organization placed under control of public authority, thus abusing real influence or supposed that gives him his quality or his mandate.');
INSERT INTO legal_articles (id, article_id, title, book, law_type, language, text)
