INSERT INTO legal_articles (id, article_id, title, book, law_type, language, text)
VALUES ('d4c1f7ce-0ad6-41d9-b580-4c13e657fdfe', '171', NULL, 'i - s imposed at any other criminal provision, except', 'penal_code', 'en', '.- Non intervention Is punished with an imprisonment of one (01} month to three (03) years and a fine of twenty thousand (20,000) to one million (1,000,000) francs or one of these two penalties only, the one who, which can prevent by its immediate action, without risk for him or for third parties, the commission of any crime or an offense against a person''s bodily integrity, abstect.');
INSERT INTO legal_articles (id, article_id, title, book, law_type, language, text)
VALUES ('a106077a-1f35-4bcd-a831-77ec4bdde848', '172', 'Refusal to innocent', 'i - s imposed at any other criminal provision, except', 'penal_code', 'en', 'Is punishable by imprisonment of one (01) months to three (03) years and a fine of twenty thousand (20,000) to one million (1,000,000) francs or one of these two penalties only, the one who, which can not accuse himself, nor accuse his spouse, his ascendants or descendants, bring to the judicial or police authorities proof of innocence of a person temporarily detained, or even condemned not Definitely for crime or offense, abstains from it.');
INSERT INTO legal_articles (id, article_id, title, book, law_type, language, text)
VALUES ('df867512-1bc7-4827-82ac-fb5c145e67cb', '173', NULL, 'i - s imposed at any other criminal provision, except', 'penal_code', 'en', '.- Failing witness (1) is punishable by imprisonment of six (06) days to three (03) months and a fine of a thousand (1,000) to fifty thousand (50,000) francs, all person regularly quoted in court to be heard as a witness which, except the case of a legitimate excuse, does not compare or refuse to lend Oath or deposit. 65 (2) is punished with imprisonment of six (06) days at one (01) year and a fine of twenty thousand (20,000) to four hundred thousand (400,000) francs or one of these two penalties only, the one who, having denounced publicly a crime or an offense and publicly declared that he knows the authors or accomplices, refuses to answer the questions asked of this regard by the competent magistrate or hides it.');
INSERT INTO legal_articles (id, article_id, title, book, law_type, language, text)
VALUES ('d22ef946-9627-487e-ad14-0ac0eb19c7e0', '174', NULL, 'i - s imposed at any other criminal provision, except', 'penal_code', 'en', '.- Required required Is punishable by a fine of twenty thousand (20,000) to five hundred thousand (500 000) francs, the one who, being regularly required as an expert, doctor or interpreter, refuses without legitimate reason to assist the authority judicial.');
INSERT INTO legal_articles (id, article_id, title, book, law_type, language, text)
VALUES ('c6da11c0-8906-41da-ae68-94f2be1fde2f', '175', 'Assessor or faulty juror', 'i - s imposed at any other criminal provision, except', 'penal_code', 'en', 'Is punishable by imprisonment of six (06) days to three (03) months and of a cing fine (5,000) to fifty thousand (50,000) francs, the assessor or the juror who, except the case of legitimate excuse, does not answer the call of his name or refuses to take the oath required by law or withdraw before the expiration of his functions.  General  Article 176.- False apologies S r Fichwer LEGISLANF and registered at 54 OPIE certified as compliant Are punished with an imprisonment of Ur (P1) J is. THE people referred to in articles 173, 174 and 175 above Quia False excuse. Section VI Non -compliance with certain judicial decisions');
INSERT INTO legal_articles (id, article_id, title, book, law_type, language, text)
VALUES ('a91b6d13-54c1-4468-997d-d154b97529ac', '177', NULL, 'i - s imposed at any other criminal provision, except', 'penal_code', 'en', '.- Prohibition of residence Is punishable by a imprisonment of one (01) month to one (01) year, the one who appears in a place that is forbidden to him or which moves away from a place which is assigned in application of article 42 of this code.');
