INSERT INTO legal_articles (id, article_id, title, book, law_type, language, text)
VALUES ('9aaf308a-a836-46b2-96f9-3928eb3429d9', '231', NULL, 'i - s imposed at any other criminal provision, except', 'penal_code', 'en', '-1- Political manifestation within a public establishment and educational environment Is punishable by imprisonment of ten (10) days to four (04) months and a fine of twenty five thousand (25,000) to two hundred and fifty thousand (250,000) francs or one of these two penalties only, the one who organizes a political manifestation within a public establishment, as well as in a school or university establishment.');
