INSERT INTO legal_articles (id, article_id, title, book, law_type, language, text)
VALUES ('641da754-3712-4486-8222-03d50e1b5ec7', '286', 'Medical interventions', 'i - s imposed at any other criminal provision, except', 'penal_code', 'en', 'Articles 277 A 281 included, are not applicable to medical acts carried out by any duly empowered person, when they are accomplished with the consent of the patient or the one who has custody of it. However, in case the patient is unable to consent, the one who has it guard or spouse must give his consent, except when it is Impossible to communicate, without risk for the patient, with them.');
